Template.body.helpers({
	isAdmin: function() {
		if(Meteor.user().profile.role === 'Admin') {
			return true;
		} else {
			return false;
		}
	}
});

Template.registerHelper("isAdmin", function () {
    if(Meteor.user().profile.role === 'Admin') {
			return true;
		} else {
			return false;
		}
});
