Meteor.publish("users", function (userSearchKeyword, limit) {
		userSearchQuery = new RegExp( userSearchKeyword, 'i' );
    return Meteor.users.find({$or: [{"emails.address": userSearchQuery}]}, {limit: limit});
});

Meteor.publish("articles", function (keyword, limit) {
	query = new RegExp( keyword, 'i' );
	return Articles.find({$or: [{body: query}, {_id: query}, {title: query}, {submitted: query}, {useremail: query}]}, {limit:limit});
});

Meteor.publish("comments", function() {
	return Comments.find();
});
