Template.submitArticle.events({
	'click button.submit': function(template) {
		var url = document.getElementById('url').value;
		var title = document.getElementById('title').value;
		var body = $('.body').code();
		var thumbnailURL = document.getElementById('thumbnailURL').value;
		var categories = [];

		$(':checkbox:checked').each(function(i){
			categories[i] = $(this).val();
		});

		Meteor.call('articleInsert', url, title, body, thumbnailURL, categories);
		document.getElementById('url').value = '';
		document.getElementById('title').value = '';
		document.getElementById('summernote').value = '';
		document.getElementById('thumbnailURL').value = '';
		Router.go('/articles');
	}
});
