Meteor.publish("users", function (userSearchKeyword, userCursor) {
		userSearchQuery = new RegExp( userSearchKeyword, 'i' );
    return Meteor.users.find({$or: [{"emails.address": userSearchQuery}]}, {limit:2, skip: userCursor});
});

Meteor.publish("invitations", function() {
	return Invitations.find();
});
