// Global Helpers.
Template.registerHelper("isAdmin", function () {
    if(Meteor.user().profile.role === 'Admin') {
			return true;
		} else {
			return false;
		}
});

Template.registerHelper("belongsToYou", function () {
    if(this._id === Meteor.userId()) {
			return true
		} else {
			return false
		}
});

Template.registerHelper("itemBelongsToYou", function () {
    if(this.user === Meteor.userId()) {
			return true
		} else {
			return false
		}
});

Template.registerHelper('truncateParagraph', function(passedString) {
    var fooText = passedString.substring(0,1); //same as truncate.
    return new Spacebars.SafeString(fooText)
});
