Template.editUserModal.events({
	'submit #edit-user-form': function(e) {
		e.preventDefault();

		var newEmail = $('#current-email').val();
		if($('#current-email').val() != "") {
			Meteor.call("editUser", newEmail, function(error) {
				if(error) {
					toastr.error("Failed to Update User... " + error);
				} else {
					toastr.success("Member Updated Successfully");
				}
			});
			// Close Modal
			$('#current-email').val("");
			$('edit-user-modal'+this._id).modal('toggle');
		} else {
			toastr.error("You must select a user role and fill out all of the user fields.");
		}
	}
});
