Meteor.methods({
	getUserCount: function () {
		console.log(Meteor.users.find().count());
		count = Meteor.users.find().count();
		return count;
	},
	setUserRole: function(userId, role) {
		Meteor.users.update({_id: userId}, {$set: {role: role}})
	}
});
