Template.editUserModal.events({
	'submit #edit-user-form': function(e) {
		e.preventDefault();

		var userId = this._id;
		console.log($('#current-email').val());
		var newUserEmail = $('#current-email').val();
		var oldEmail = this.emails[0].address;
		if($('#current-email').val() != "") {
			Meteor.call("editUser", userId, newUserEmail, oldEmail, function(error) {
				if(error) {
					toastr.error("Failed to Update User... " + error);
				} else {
					toastr.success("Member Updated Successfully");
					// Close Modal
					$('#current-email').val("");
					$('#edit-user-modal'+this._id).modal('toggle');
				}
			});
		} else {
			toastr.error("You must fill out all of the user fields.");
		}
	}
});
